$(document).ready(function(){$(".gallery-clear").click(function(n){n.preventDefault();$(".image-gallery .card").each(function(){$(this).hasClass("selected")&&$(this).removeClass("selected")})});$(".gallery-upload").click(function(n){n.preventDefault();$("#imageName").val()==""||$("#imagePath").get(0).files.length===0?$("#gallery-save-modal").modal("show"):$(".upload-form").submit()});$(".upload-dialog").click(function(n){n.preventDefault();$("#gallery-upload-modal").modal("show")});$(function(){$("#imagePath:file").change(function(){var n=$(this).val(),t=n.replace(/^.*[\\\/]/,"");$("#imageName").val(t);$("#imageDir").val(n)})})});